import { useEffect, useState } from "react";
import { Form, Button } from "react-bootstrap";

const ReservationForm = () => {
  const [reservation, setReservation] = useState({
    name: "",
    phone: "",
    numberOfPersons: 1,
    smoking: false,
    dateTime: "",
    specialRequests: "",
  });

  const inputChange = (e: any) => {
    let id = e.target.id;
    setReservation({
      ...reservation,
      [id]: id === "smoking" ? e.target.checked : e.target.value,
    });
  };

  useEffect(() => {}, [reservation]);

  const submitReservation = async (e: EventTarget) => {
    e.preventDefault();

    try {
      let response = await fetch(
        "https://striveschool.herokuapp.com/api/reservation",
        {
          method: "POST",
          body: JSON.stringify(reservation),
          headers: {
            "Content-type": "application/json",
          },
        }
      );
      if (response.ok) {
        alert("Reservation saved!");
        setReservation({
          name: "",
          phone: "",
          numberOfPersons: 1,
          smoking: false,
          dateTime: "",
          specialRequests: "",
        });
      } else {
        alert("Houston we had a problem, try again!");
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <>
      <h3 className="mt-3">RESERVATION FORM</h3>
      <Form className="mb-5" onSubmit={(e) => submitReservation(e)}>
        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter name"
            value={reservation.name}
            id="name"
            onChange={(e) => inputChange(e)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Phone</Form.Label>
          <Form.Control
            type="number"
            placeholder="Enter phone"
            id="phone"
            value={reservation.phone}
            onChange={(e) => inputChange(e)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>How many people?</Form.Label>
          <Form.Control
            as="select"
            value={reservation.numberOfPersons}
            id="numberOfPersons"
            onChange={(e) => inputChange(e)}
          >
            <option>1</option>
            <option>2</option>
            <option>3</option>
            <option>4</option>
            <option>5</option>
            <option>6</option>
          </Form.Control>
        </Form.Group>
        <Form.Group>
          <Form.Check
            type="checkbox"
            label="Do you smoke?"
            checked={reservation.smoking}
            id="smoking"
            onChange={(e) => inputChange(e)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Date</Form.Label>
          <Form.Control
            type="datetime-local"
            value={reservation.dateTime}
            id="dateTime"
            onChange={(e) => inputChange(e)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Any special request?</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            value={reservation.specialRequests}
            id="specialRequests"
            onChange={inputChange}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </>
  );
};

export default ReservationForm;
